import { configureStore } from "@reduxjs/toolkit";
import toggleCartReducer from "./show-cart";
import cartRedecer from "./cart-slice";

const store = configureStore({
  reducer: { toggleCart: toggleCartReducer,cart:cart },
});

export default store;
