import React from "react";
import Card from "../UI/Card";
import "./add-contact.css";
import Button from "../UI/Button";
import { useState } from "react";

const AddContact = () => {
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [nameIsValid, setNameIsValid] = useState({ status: true, message: "" });
  const [phoneIsValid, setPhoneIsValid] = useState({
    status: true,
    message: "",
  });

  const nameChangeHandler = (e) => {
    setName(e.target.value);
    if (e.target.value !== "") {
      setNameIsValid({ status: true });
    }
  };

  const phoneChangeHandler = (e) => {
    setPhone(e.target.value);
    if (e.target.value !== "") {
      setPhoneIsValid({ status: true });
    } else if (e.target.value.length === 11) {
      setPhoneIsValid({ status: true });
    }
  };

  const submitHandler = (e) => {
    e.preventDefault();
    if (name === "") {
      setNameIsValid({ status: false });
      setNameIsValid({ message: "name must not be empty!" });
      return;
    } else if (phone === "" || phone.trim().length < 11) {
      setPhoneIsValid({});
      return;
    }
    console.log(name, phone);
  };

  const nameClasses = !nameIsValid.status
    ? "form-control invalid"
    : "form-control";
  const phoneClasses = !phoneIsValid.status
    ? "form-control invalid"
    : "form-control";

  return (
    <Card className="add-contact">
      <form onSubmit={submitHandler}>
        <div className={nameClasses}>
          <label htmlFor="name">Name</label>
          <input value={name} onChange={nameChangeHandler} type="text" />
          {!nameIsValid.status && (
            <p className="text-error"> {nameIsValid.message} </p>
          )}
        </div>
        <div className={phoneClasses}>
          <label htmlFor="phone">Phone</label>
          <input value={phone} onChange={phoneChangeHandler} type="number" />
          {!phoneIsValid.status && (
            <p className="text-error">{phoneIsValid.message} </p>
          )}
        </div>
        <Button type="submit" className="add-button">
          Submit
        </Button>
      </form>
    </Card>
  );
};

export default AddContact;
