import React from "react";
import Card from "../UI/Card";
import "./add-contact.css";
import Button from "../UI/Button";
import { useState } from "react";

const AddContact = () => {
  const [name, setName] = useState("");
  const [phone, setPhone] = useState("");
  const [nameIsValid, setNameIsValid] = useState({ status: true, message: "" });
  const [phoneIsValid, setPhoneIsValid] = useState(true);

  const nameChangeHandler = (e) => {
    setName(e.target.value);
    if (e.target.value !== "") {
      setNameIsValid(true);
    }
  };

  const phoneChangeHandler = (e) => {
    setPhone(e.target.value);
    if (e.target.value !== "") {
      setPhoneIsValid(true);
    }
  };

  const submitHandler = (e) => {
    e.preventDefault();
    if (name === "") {
      setNameIsValid(name);
      return;
    } else if (phone === "" || phone.trim().length < 11) {
      setPhoneIsValid(false);
      return;
    }
    console.log(name, phone);
  };

  const nameClasses = !nameIsValid ? "form-control invalid" : "form-control";
  const phoneClasses = !phoneIsValid ? "form-control invalid" : "form-control";

  return (
    <Card className="add-contact">
      <form onSubmit={submitHandler}>
        <div className={nameClasses}>
          <label htmlFor="name">Name</label>
          <input value={name} onChange={nameChangeHandler} type="text" />
          {!nameIsValid && (
            <p className="text-error">Please enter valid input</p>
          )}
        </div>
        <div className={phoneClasses}>
          <label htmlFor="phone">Phone</label>
          <input value={phone} onChange={phoneChangeHandler} type="number" />
          {!phoneIsValid && (
            <p className="text-error">Please enter valid input</p>
          )}
        </div>
        <Button type="submit" className="add-button">
          Submit
        </Button>
      </form>
    </Card>
  );
};

export default AddContact;
