import { createSlice } from "@reduxjs/toolkit";

const showCart = createSlice({
  name: "show-cart",
  initialState: { showCart: false },
  reducers: {
    showCart(state) {
      state.showCart = !state.showCart;
    },
  },
});

export const showCartAction = showCart.actions;

export default showCart.reducer;
